import React, { useState } from 'react';

class BattleshipBoard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {shipPositions: []}
  }

  generateShip = () => {

     this.setState({shipPositions: [3]})
  }

  render() {
    return (
      <div>
      {this.state.shipPositions}
        <button onClick={this.generateShip}>
          Click me
        </button>
      </div>
    );
  }
}


export default BattleshipBoard;
